import React, { useState, useEffect } from "react";
import axios from "axios";
import moment from "moment";
import Chart from "react-apexcharts";
import { Link } from "react-router-dom";
import Footer from "./Footer";

const Home = () => {
  const [jobs, setJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [currtime, setCurrTime] = useState(new Date());
  const today = moment().format("YYYY-MM-DD");

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrTime(new Date());
    }, 60000); // Update every minute
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    axios
      .get("http://localhost:5000/jobs")
      .then((response) => setJobs(response.data))
      .catch((error) => console.error("Error fetching job data:", error));
  }, []);

  const calculateChartData = (job) => {
    if (!job) return [0, 100];
    const startTime = new Date(job.start_time);
    const endTime = new Date(job.end_time);
    const nowTime = new Date(currtime);

    if (nowTime < startTime) return [0, 100]; // Task hasn't started
    if (nowTime > endTime) return [100, 0]; // Task is completed

    const totalDuration = endTime - startTime;
    const completedDuration = nowTime - startTime;
    const completedPercentage = (completedDuration / totalDuration) * 100;
    const leftPercentage = 100 - completedPercentage;

    return [completedPercentage, leftPercentage];
  };

  const [chartConfig, setChartConfig] = useState({
    type: "pie",
    width: 150,
    height: 150,
    series: [0, 100],
    options: {
      chart: {
        toolbar: { show: false },
      },
      labels: ["Completed", "Remaining"],
      dataLabels: {
        enabled: true,
        formatter: (val) => `${val.toFixed(1)}%`,
      },
      colors: ["#00897b", "#ff8f00"],
      legend: { show: true },
    },
  });

  useEffect(() => {
    if (selectedJob) {
      setChartConfig((prevConfig) => ({
        ...prevConfig,
        series: calculateChartData(selectedJob),
      }));
    }
  }, [selectedJob, currtime]);

  const handleSelectEvent = (job) => {
    setSelectedJob(job);
  };

  return (
    <div>
      <div className="flex h-[90vh]">
        <aside className="w-[20vw] bg-[#4A4752] text-white p-6 flex flex-col justify-between">
          <h1 className="text-4xl font-bold text-[#359BD2] text-center">AVILAMBH</h1>
          <nav className="mt-6 space-y-4 pt-24 pl-8">
            <Link to="/home" className="block text-gray-300 hover:text-white underline">HOME</Link>
            <Link to="/Jobs" className="block text-gray-300 hover:text-white">JOB SCHEDULE</Link>
            <Link to="/reports" className="block text-gray-300 hover:text-white">REPORTS</Link>
            <Link to="/safety" className="block text-gray-300 hover:text-white">SAFETY</Link>
            <Link to="/transport" className="block text-gray-300 hover:text-white">TRANSPORT</Link>
            <Link to="/inventory" className="block text-gray-300 hover:text-white">INVENTORY</Link>
          </nav>
        </aside>
      </div>
      <div className="w-[80vw] h-[80vh] fixed top-0 right-0">
        <div className="w-[60vw] h-[40vh] bg-slate-500 mx-auto my-6 grid grid-cols-5">
          <div className="p-6 col-span-2 mx-auto">
            {selectedJob ? selectedJob.task : "Task"}
            <Chart {...chartConfig} />
          </div>
          <div className="h-[40vh] p-4 col-span-3">
            <h2 className="text-2xl font-bold mb-4">Today's Job Schedule</h2>
            <div className="h-[30vh] overflow-y-auto scrollbar-hide">
              <div className="grid grid-cols-4 gap-4">
                {jobs.length > 0 ? (
                  jobs
                    .filter((job) => moment(job.start_time).isSame(today, "day"))
                    .map((job) => (
                      <div
                        key={job.job_id}
                        className="h-[15vh] p-3 bg-blue-500 text-white rounded-lg cursor-pointer"
                        onClick={() => handleSelectEvent(job)}
                      >
                        <strong>{job.task}</strong>
                        <br />
                        <span className="text-xs">
                          from: {moment(job.start_time).format("hh:mm A")}
                          <br />
                          to: {moment(job.end_time).format("hh:mm A")}
                        </span>
                      </div>
                    ))
                ) : (
                  <p>No jobs scheduled for today.</p>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
